export declare type NonEmptyArray<T> = Array<T> & {
    readonly 0: T;
};
export declare const isNonEmptyArray: <T>(list: T[]) => list is NonEmptyArray<T>;
export declare const head: <T>(l: NonEmptyArray<T>) => T;
export declare const tail: <T>(l: NonEmptyArray<T>) => T[];
export declare const concat: <A>(x: NonEmptyArray<A>, y: NonEmptyArray<A>) => NonEmptyArray<A>;
